import React, { Component } from "react";
import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, Name, Email, Selection, Wish, Photo, TextArea, FormBtn } from "../components/Form";
import "../style.css"

class Requests extends Component {
  state = {
    books: [],
    synopsis: "",
    name: "",
    email: "",
    selection: "",
    wish: "",
    photo: ""
  };

  componentDidMount() {
    this.loadBooks();
  }

  loadBooks = () => {
    API.getBooks()
      .then(res =>
        this.setState({ books: res.data, title: "", author: "", synopsis: "", name: "", email: "", selection: "", wish: "", photo: "" })
      )
      .catch(err => console.log(err));
  };

  deleteBook = id => {
    API.deleteBook(id)
      .then(res => this.loadBooks())
      .catch(err => console.log(err));
  };

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    if (this.state.name && this.state.email && this.state.selection) {
      API.saveBook({
        title: this.state.title,
        author: this.state.author,
        synopsis: this.state.synopsis,
        name: this.state.name,
        email: this.state.email,
        selection: this.state.selection,
        wish: this.state.wish,
        photo: this.state.photo
      })
        .then(res => this.loadBooks())
        .catch(err => console.log(err));
    }
  };

  render() {
    return (
      <Container>
        <Row>
          <Col size="md-12">
            <form id="form">
              <Input
                value={this.state.title}
                onChange={this.handleInputChange}
                name="title"
                placeholder="Title (required)"
              />
              <Input
                value={this.state.author}
                onChange={this.handleInputChange}
                name="author"
                placeholder="Author (required)"
              />
              <Name
                value={this.state.name}
                onChange={this.handleInputChange}
                name="name"
                placeholder="Author (required)"
              />
              <Email
                value={this.state.email}
                onChange={this.handleInputChange}
                name="email"
                placeholder="Author (required)"
              />
              <Selection
                value={this.state.selection}
                onChange={this.handleInputChange}
                name="selection"
                placeholder="Author (required)"
              />
              <Wish
                value={this.state.wish}
                onChange={this.handleInputChange}
                name="wish"
                placeholder="Author (required)"
              />
              <Photo
                value={this.state.photo}
                onChange={this.handleInputChange}
                name="photo"
                placeholder="Author (required)"
              />
              <FormBtn
                disabled={!(this.state.author && this.state.title)}
                onClick={this.handleFormSubmit}
              >
                Submit Request
              </FormBtn>
            </form>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Requests;
